import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import LearningSection from './components/LearningSection';
import HomeSection from './components/HomeSection';
import AboutSection from './components/AboutSection';
import VolunteerSection from './components/VolunteerSection';
import ContactSection from './components/ContactUsSection';
import ProjectSection from './components/ProjectSection';

import Navbar_french from './components_french/Navbar';
import LearningSection_french from './components_french/LearningSection';
import HomeSection_french from './components_french/HomeSection';
import AboutSection_french from './components_french/AboutSection';
import VolunteerSection_french from './components_french/VolunteerSection';
import ContactSection_french from './components_french/ContactUsSection';
import ProjectSection_french from './components_french/ProjectSection';

import './App.css';

function App() {
  const [showFrench, setShowFrench] = useState(false);

  const toggleLanguage = () => {
    setShowFrench(!showFrench);
  };

  const renderNavbar = () => {
    return showFrench ? <Navbar_french /> : <Navbar />;
  };

  const renderHomeSection = () => {
    return showFrench ? <HomeSection_french /> : <HomeSection />;
  };

  const renderAboutSection = () => {
    return showFrench ? <AboutSection_french /> : <AboutSection />;
  };

  const renderProjectSection = () => {
    return showFrench ? <ProjectSection_french /> : <ProjectSection />;
  };

  const renderLearningSection = () => {
    return showFrench ? <LearningSection_french /> : <LearningSection />;
  };

  const renderVolunteerSection = () => {
    return showFrench ? <VolunteerSection_french /> : <VolunteerSection />;
  };

  const renderContactSection = () => {
    return showFrench ? <ContactSection_french /> : <ContactSection />;
  };

  return (
    <Router>
      {renderNavbar()}

      <Switch>
        <Route exact path="/">
          {renderHomeSection()}
        </Route>
        <Route path="/about">
          {renderAboutSection()}
        </Route>
        <Route path="/projects">
          {renderProjectSection()}
        </Route>
        <Route path="/learning">
          {renderLearningSection()}
        </Route>
        <Route path="/volunteer">
          {renderVolunteerSection()}
        </Route>
        <Route path="/contact">
          {renderContactSection()}
        </Route>
      </Switch>

      <button onClick={toggleLanguage}>
        {showFrench ? 'Show English' : 'Show French'}
      </button>
    </Router>
  );
}

export default App;
